import axios from 'axios';

const API_URL = 'http://localhost:3000/api';

export const getDrivers = () => {
    return axios.get(`${API_URL}/drivers`);
};

export const addDrivers = (data: any) => {
    return axios.post(`${API_URL}/drivers`, data);
};
