import React from 'react';
import { Trip } from '../../types/trip';
import './list.css'

interface TripListProps {
    trips: Trip[];
}

export const TripList: React.FC<TripListProps> = ({ trips }) => {
    const [selectedTrip, setSelectedTrip] = React.useState<Trip | null>(null);
    const handleTripClick = (trip: Trip) => {
        setSelectedTrip(trip);
    }
    return (
        <div>
        <ul>
            {trips.map((trip) => (
                <li key={trip.id} className='trip-list' onClick={() => handleTripClick(trip)}>
                    <strong>{trip.firstName} {trip.lastName}</strong> {trip.phoneNumber} - {trip.route}
                </li>
            ))}
        </ul>
        {selectedTrip && (
            <div>
                <h3>Выбранный трип:</h3>
                <p>{selectedTrip.firstName} {selectedTrip.lastName}</p>
                <p>{selectedTrip.phoneNumber}</p>
                <p>{selectedTrip.route}</p>
            </div>
        )}
    </div>
    );
};
